mapper.insert:
INSERT INTO house(
    user_id, title, area, rent, type_a, type_b, type_c, type_d, style, pay_a, pay_b, info, addr_id, addr_detail, create_time, status
) VALUES(
    #{user_id},#{title},#{area},#{rent},#{type_a},#{type_b},#{type_c},#{type_d},#{style},#{pay_a},#{pay_b},#{info},#{addr_id},#{addr_detail},#{create_time},#{status}
);

mapper.select:
SELECT id,user_id, title, area, rent, type_a, type_b, type_c, type_d, style, pay_a, pay_b, info, addr_id, addr_detail, create_time,status FROM house


        AND id = #{id}


        AND user_id = #{user_id}


        AND type_a = #{type_a}


        AND type_b = #{type_b}


        AND type_c = #{type_c}


        AND type_d = #{type_d}


        AND rent < ${rent}


        pay_a = #{pay_a}


        pay_b = #{pay_b},
